import React from "react";
import {
  formatDistance,
  differenceInDays,
  formatDistanceToNow,
} from "date-fns";
import { tr } from "date-fns";
const Task = ({ taskObj, onComplete }) => {
  const date = new Date(taskObj.deadline);
  const today = new Date();
  const taskDate = formatDistanceToNow(date, { locale: tr });
  return (
    <div className="task">
      <h3>{taskObj.title}</h3>
      <div className="deadline">
        son teslim: <span>{taskObj.deadline}</span>
      </div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>
            {p}
          </span>
        ))}
      </div>
      {onComplete && (
        <button onClick={() => onComplete(taskObj.id)}>TamamlandÄ±</button>
      )}
    </div>
  );
};

export default Task;
